INSERT INTO `articles` (`id`, `title`, `image`, `text`) VALUES
(2, 'Jacques Prévert, Artiste, écrivain, Poète, Scénariste (1900 - 1977).', '', 'Paris est tout petit pour ceux qui s\\''aiment d\\''un aussi grand amour. '),
(3, 'Ernest Hemingway, écrivain, journaliste (1899 -1961)', '', 'Il n’y avait jamais de fin à Paris et le souvenir qu’en gardent tous ceux qui y ont vécu diffère d’une personne à l’autre. (...) Paris valait toujours la peine, et vous receviez toujours quelque chose en retour de ce que vous lui donniez. Mais tel était le Paris de notre jeunesse, au temps où nous étions très pauvres et très heureux.'),
(4, 'Stefan Zweig, écrivain, journaliste, dramaturge (1881 -1942).', '', 'Paris, c’est le luxe, l’élégance, l’entrain, la légèreté, la joie, l’« antiprovince », la liberté, et avant tout les femmes, beaucoup de femmes.'),
(5, 'Victor Hugo, poète, dramaturge, écrivain (1802 - 1885)', '', 'Il faut l''aimer, il faut la vouloir, il faut la subir, cette ville frivole, légère, chantante, dansante, fardée, fleurie, redoutable, qui, nous l''avons dit, à qui la prend donne la puissance, que Maximilien, aïeul de Charles Quint, aurait payée de tout son empire, que les Girondins auraient achetée de leur sang et que Henri IV eut pour une messe. Ses lendemains sont toujours bons. La folie de Paris, cuvée, est sagesse.');
INSERT INTO `blog` (`id`, `title`, `date`, `text`, `images`, `image`) VALUES
(1, 'MOULIN ROUGE', '15 Mai 2015', 'Moulin Rouge est un cabaret à Paris, France construit en 1889 par Joseph Oller. Près de Montmartre, dans le quartier de Pigalle à Paris sur le boulevard de Clichy dans le 18ème arrondissement, il est marqué par le moulin à vent rouge sur son toit. Une architecture de salle révolutionnaire qui permettait des changements de décor rapides et où tous les publics se mélangeaient. Des soirées festives, au champagne, où l’on danse et rit énormément grâce à des attractions pleines d’humour et qui changent régulièrement, comme celle du Pétomane. Surnommé « Le Premier Palais des Femmes » par Oller et Zidler, le cabaret connait rapidement un vif succès.', '', 'moulinRouge.jpg'),
(2, 'BASILIQUE DU SACRÉ-COEUR', '16 Mai 2015', 'Avec plus de 10 millions de pèlerins et de touristes chaque année, le Sacré-Cœur est le deuxième monument français le plus visité après la cathédrale Notre-Dame-de-Paris. Construite selon un « vœu national » de l’Église catholique pour expier les crimes de la Commune de Paris, sur la Butte Montmartre, la basilique du Sacré-Cœur fut édifiée de 1875 à 1914.', '', 'sacreCoeur.jpg'),
(3, 'PANTHÉON', '17 Mai 2015', 'Situé dans le 5ème arrondissement, sur la Montagne Sainte-Geneviève, le Panthéon surplombe tout Paris. Son architecte, Jacques-Germain Soufflot, avait l\\''intention de combiner la légèreté et la luminosité de la cathédrale gothique avec les principes classiques. Soufflot meurt avant que son travail a été réalisé, et ses plans ont été pas entièrement suivi. La transparence qu\\''il avait prévu pour son chef-d\\''œuvre n\\''a pas été atteint. Néanmoins, il est l\\''une des plus importantes réalisations architecturales de son temps et le premier grand monument néoclassique.', '', 'pantheon.jpg'),
(4, 'PALAIS DU LUXEMBOURG', '18 Mai 2015', 'Le Palais du Luxembourg est le siège du Sénat. Construit à partir de 1615 pour Marie de Médicis, il a connu des évolutions architecturales qui l\\''ont peu à peu adapté aux besoins d\\''une assemblée politique moderne tout en respectant scrupuleusement ce trésor exceptionnel d\\''architecture parisienne, qui abrite, en outre, un patrimoine artistique exceptionnel.', '', 'palaisLux.jpg');
