INSERT INTO `ps_product_lang` (`id_product`, `id_shop`, `id_lang`, `description`, `description_short`, `link_rewrite`, `meta_description`, `meta_keywords`, `meta_title`, `name`, `available_now`, `available_later`) VALUES
(1, 1, 1, '<p><span>Coupez-vous du monde et laissez-vous envahir par la musique, ou bien laissez le monde qui vous entoure venir jusqu’à vous. Vous avez le choix avec les premiers écouteurs à réduction de bruit de Bose.</span></p>', '<p><span>Les écouteurs QuietComfort® 20 sont l’assurance de profiter d’un meilleur son chaque jour, partout.</span></p>', 't-shirt-delave-manches-courtes', '', '', '', 'QuietComfort®20', 'En stock', ''),
(2, 1, 1, '<p>Fashion propose des vêtements de qualité depuis 2010. La marque propose une gamme féminine composée d''élégants vêtements à coordonner et de robes originales et offre désormais une collection complète de prêt-à-porter, regroupant toutes les pièces qu''une femme doit avoir dans sa garde-robe. Fashion se distingue avec des looks à la fois cool, simples et rafraîchissants, alliant élégance et chic, pour un style reconnaissable entre mille. Chacune des magnifiques pièces de la collection est fabriquée avec le plus grand soin en Italie. Fashion enrichit son offre avec une gamme d''accessoires incluant chaussures, chapeaux, ceintures et bien plus encore !</p>', '<p><span>Voici des écouteurs qui ne laisseront rien s’interposer entre vous et votre musique. Compacts et légers, les écouteurs intra-auriculaires Bose® SoundTrue® Ultra offrent un son riche et profond sur une vaste plage de fréquences. Leur isolation phonique vous plonge au cœur de votre musique afin que vous puissiez entendre chaque note.</span></p>', 'chemisier', '', '', '', 'Bose® SoundTrue® Ultra', 'En stock', ''),
(3, 1, 1, '<p>B<span style="color:#0f0101;">ose Corporation est une entreprise américaine d''électronique basée à <span style="color:#0f0101;">Framingham</span>dans le <span style="color:#0f0101;">Massachusetts</span>, fondée en <span style="color:#0f0101;">1964</span> par <span style="color:#0f0101;">Amar G. Bose</span>. </span>Bose se spécialise dans le développement, la construction et la vente d''<span style="color:#0f0101;">enceintes</span>, de systèmes audio domestiques, professionnels et embarqués (pour les automobiles et les bateaux), de technologies d''<span style="color:#0f0101;">isolation acoustique</span> pour l''<span style="color:#0f0101;">industrie aéronautique</span> et de<span style="color:#0f0101;">logiciels</span> informatiques de <span style="color:#0f0101;">simulation</span> acoustique.</p>', '<p><span>Dynamisez vos entraînements avec un son riche et équilibré comme jamais auparavant. Les écouteurs intra-auriculaires SoundSport® résistent à la transpiration et restent confortablement en place, même en cas d’effort intense.</span></p>', 'robe-imprimee', '', '', '', 'Bose® SoundSport®', 'En stock', ''),
(4, 1, 1, '<p>Bose Corporation est une entreprise américaine d''électronique basée à Framinghamdans le Massachusetts, fondée en 1964 par Amar G. Bose. Bose se spécialise dans le développement, la construction et la vente d''enceintes, de systèmes audio domestiques, professionnels et embarqués (pour les automobiles et les bateaux), de technologies d''isolation acoustique pour l''industrie aéronautique et delogiciels informatiques de simulation acoustique.</p>', '<p><span>Les écouteurs SoundTrue™ vous offrent la clarté audio et l’élégance dont vous avez besoin pour vos déplacements. Profitez d’une musique plus claire et plus profonde que jamais. Par ailleurs, les embouts StayHear® en silicone épousent délicatement vos oreilles et restent en place tout au long de la journée. Vivez pleinement votre style et votre musique grâce aux écouteurs SoundTrue™.</span></p>', 'robe-imprimee', '', '', '', 'SoundTrue', 'En stock', ''),
(5, 1, 1, '<p>Fashion propose des vêtements de qualité depuis 2010. La marque propose une gamme féminine composée d''élégants vêtements à coordonner et de robes originales et offre désormais une collection complète de prêt-à-porter, regroupant toutes les pièces qu''une femme doit avoir dans sa garde-robe. Fashion se distingue avec des looks à la fois cool, simples et rafraîchissants, alliant élégance et chic, pour un style reconnaissable entre mille. Chacune des magnifiques pièces de la collection est fabriquée avec le plus grand soin en Italie. Fashion enrichit son offre avec une gamme d''accessoires incluant chaussures, chapeaux, ceintures et bien plus encore !</p>', '<p><span>Liberté. Commodité. Avec Bose®, les principaux avantages d’un casque sans fil prennent une nouvelle dimension. Profitez d’un son profond et immersif quel que soit le volume, et d’une clarté accrue pour les deux interlocuteurs lors de vos appels.</span></p>', 'robe-ete-imprimee', '', '', '', 'Bose® SoundLink® II', 'En stock', ''),
(6, 1, 1, '<p>Fashion propose des vêtements de qualité depuis 2010. La marque propose une gamme féminine composée d''élégants vêtements à coordonner et de robes originales et offre désormais une collection complète de prêt-à-porter, regroupant toutes les pièces qu''une femme doit avoir dans sa garde-robe. Fashion se distingue avec des looks à la fois cool, simples et rafraîchissants, alliant élégance et chic, pour un style reconnaissable entre mille. Chacune des magnifiques pièces de la collection est fabriquée avec le plus grand soin en Italie. Fashion enrichit son offre avec une gamme d''accessoires incluant chaussures, chapeaux, ceintures et bien plus encore !</p>', '<p><span>Liberté. Commodité. Avec Bose®, les principaux avantages d’un casque sans fil prennent une nouvelle dimension. Profitez d’un son profond et immersif quel que soit le volume, et d’une clarté accrue pour les deux interlocuteurs lors de vos appels. </span></p>', 'robe-ete-imprimee', '', '', '', 'Casque circum-aural sans fil Bose® SoundLink® II', 'En stock', ''),
(7, 1, 1, '<p>Fashion propose des vêtements de qualité depuis 2010. La marque propose une gamme féminine composée d''élégants vêtements à coordonner et de robes originales et offre désormais une collection complète de prêt-à-porter, regroupant toutes les pièces qu''une femme doit avoir dans sa garde-robe. Fashion se distingue avec des looks à la fois cool, simples et rafraîchissants, alliant élégance et chic, pour un style reconnaissable entre mille. Chacune des magnifiques pièces de la collection est fabriquée avec le plus grand soin en Italie. Fashion enrichit son offre avec une gamme d''accessoires incluant chaussures, chapeaux, ceintures et bien plus encore !</p>', '<p><span>Un casque circum-aural qui renvoie votre musique vers l’intérieur et vous permet d’afficher votre style à votre entourage. Le casque SoundTrue® II donne vie à votre musique avec une profondeur et une clarté qu’un grand nombre de casques sur le marché sont bien loin de pouvoir égaler.</span></p>', 'robe-mousseline-imprimee', '', '', '', 'Casque circum-aural Bose® SoundTrue® II', 'En stock', ''),
(8, 1, 1, '', '', 'quietcomfort-25', '', '', '', 'QuietComfort® 25', '', ''),
(9, 1, 1, '', '<p><span>Coupez le cordon et choisissez librement votre musique avec le premier casque audio supra-aural sans fil de Bose. Profitez d’un son puissant et clair et basculez facilement entre musique et appels grâce à des commandes intuitives. </span></p>', 'casque-supra-aural-bluetooth-bose-soundlink', '', '', '', 'Casque supra-aural Bluetooth® Bose® SoundLink®', '', ''),
(10, 1, 1, '', '<p><span>Le casque d’aviation A20® a été conçu pour offrir davantage de confort et proposer une plus grande réduction de bruit que tout autre casque jamais imaginé par Bose. Il garantit également le son d’une grande clarté caractéristique de notre marque.</span></p>', 'casque-d-aviation-a20-double-jacks-avion-bluetooth', '', '', '', 'Casque d''Aviation A20® double jacks avion, Bluetooth®', '', ''),
(11, 1, 1, '', '<p><span>Profitez de votre musique lors de vos déplacements. L’enceinte</span><em>Bluetooth</em><span>® SoundLink® Mini II diffuse un son riche et naturel, avec des basses d’une profondeur surprenante pour un modèle ultra-compact qui tient dans le creux de la main.</span></p>', 'enceinte-bluetooth-soundlink-mini-ii', '', '', '', 'Enceinte Bluetooth® SoundLink® Mini II', '', ''),
(12, 1, 1, '<h3>En tant que mélomane, vous ne cessez de découvrir de nouveaux titres et d’écouter vos artistes préférés en streaming. Le système audio sans fil SoundTouch® 30 vous permet de les écouter comme jamais auparavant. Il se connecte directement à votre réseau Wi-Fi® domestique sans équipements complexes. </h3>', '<h3>Le système audio sans fil le plus performant de Bose</h3>', 'systeme-audio-sans-fil-soundtouch-30-serie-iii', '', '', '', 'Système audio sans fil SoundTouch® 30 série III', '', ''),
(13, 1, 1, '<p><span>L’enceinte </span><em>Bluetooth</em><span>® SoundLink® III est notre enceinte mobile </span><em>Bluetooth</em><span>® la plus performante. Ce modèle est plus puissant et offre une autonomie supérieure à celle de son célèbre prédécesseur, tout en utilisant les technologies Bose® qui permettent de diffuser votre musique avec un son riche, profond et clair. </span></p>', '<p><span>Voici l’enceinte portable qui rassemblera vos amis autour de votre musique, tout en affichant votre style.</span></p>', 'enceinte-bluetooth-soundlink-iii-avec-cache-souple', '', '', '', 'Enceinte Bluetooth® SoundLink® III avec cache souple', '', ''),
(14, 1, 1, '<p><span>Pourquoi faire compliqué ? Enrichissez radicalement le son de votre téléviseur en toute simplicité. Le système CineMate® 15 vous offre le son net et ample qui vous manquait jusqu’à présent, et des basses profondes dont vous ne soupçonniez pas l’existence, le tout à partir d’une barre de son compacte et d’un module Acoustimass® dissimulable.</span></p>', '', 'systeme-d-enceintes-home-cinema-cinemate-15', '', '', '', 'Système d''enceintes home cinéma CineMate® 15', '', ''),
(15, 1, 1, '<p><span>Voici le système de barre de son Bose® bénéficiant de nos technologies les plus avancées. Cette barre de son de 94 cm diffuse un son clair et spacieux dans toute la pièce, tandis que le module Acoustimass® sans fil offre des basses puissantes.</span></p>', '', 'systeme-home-cinema-bose-lifestyle-soundtouch-135', '', '', '', 'Système home cinéma Bose® Lifestyle® SoundTouch® 135', '', ''),
(16, 1, 1, '<p><span>Des technologies innovantes. Le gage d’un système home cinéma plus performant à la maison. Grâce au système home cinéma Lifestyle® SoundTouch® 235. Ses enceintes compactes diffusent un son home cinéma plus imposant que l’image, tandis que le module Acoustimass® exclusif restitue des basses puissantes.</span></p>', '', 'systeme-home-cinema-bose-lifestyle-soundtouch-235', '', '', '', 'Système home cinéma Bose® Lifestyle® SoundTouch® 235', '', ''),
(17, 1, 1, '<p><span>Si vous souhaitez bénéficier de la meilleure expérience qui soit en matière de home cinéma, ne cherchez plus. Le système Lifestyle® 535 combine nos technologies les plus avancées, simplicité, versatilité et élégance.</span></p>', '', 'systeme-home-cinema-bose-lifestyle-soundtouch-535', '', '', '', 'Système home cinéma Bose® Lifestyle® SoundTouch® 535', '', ''),
(18, 1, 1, '<p><span>Pour tous vos programmes qui ne peuvent pas attendre : films, séries ou morceaux de musique. Un meilleur son qui les révèle entièrement. Le système home cinéma Bose® SoundTouch® 220 donne vie aux films, aux séries et à la musique sans fil grâce à deux petites enceintes Gemstone® et à un module de basses Acoustimass® dissimulable.</span></p>', '', 'systeme-home-cinema-bose-soundtouch-220', '', '', '', 'Système home cinéma Bose® SoundTouch® 220', '', ''),
(19, 1, 1, '<p><span>Si vous recherchez l’expérience audio pour ordinateur la plus réaliste de Bose, ces enceintes sont faites pour vous. Le système Companion 50 vous immerge dans un environnement acoustique spectaculaire, digne d’un système à cinq enceintes.</span></p>', '', 'systeme-d-enceintes-multimedia-companion-50', '', '', '', 'Système d''enceintes multimédia Companion® 50', '', '');
